#| code-summary: "Show code which generates figure"
df %>% 
  ggplot(aes(sample = value)) +
  stat_qq_line(color = "red") +
  geom_qq(size= 0.5) + 
  facet_wrap(~key, scales = "free") +
  theme_minimal() +
  labs(x = "Quantiles of Simulated Estimates", y = "Quantiles of Normal Distribution")
